from api import dbfrom api import maclass VoucherModel(db.Model):    """    Model class representing the 'voucher_model' table in the database.    Attributes:        id (int): Primary key for the voucher.        code (str): Unique code associated with the voucher.        type (str): Type of the voucher (e.g., gift voucher, discount voucher).        value (float): Monetary value of the voucher.        discount (float): Discount amount of the voucher.        expiration_date (Date): Date when the voucher expires.        redemption_limit (int): Maximum number of times the voucher can be redeemed.        user_restrictions (str): Restrictions on users who can redeem the voucher.        products (str): Products associated with the voucher.    """    id = db.Column(db.Integer, primary_key=True)    code = db.Column(db.String)      type = db.Column(db.String)      value = db.Column(db.Float)      discount = db.Column(db.Float)      expiration_date = db.Column(db.Date)    redemption_limit = db.Column(db.Integer)    user_restrictions = db.Column(db.String)    products = db.Column(db.String)    def __init__(self, code, type, value, discount, expiration_date, redemption_limit, user_restrictions, products):        """        Constructor for the VoucherModel class.        Args:            id (int): Primary key for the voucher.            code (str): Unique code associated with the voucher.            type (str): Type of the voucher (e.g., gift voucher, discount voucher).            value (float): Monetary value of the voucher.            discount (float): Discount amount of the voucher.            expiration_date (Date): Date when the voucher expires.            redemption_limit (int): Maximum number of times the voucher can be redeemed.            user_restrictions (str): Restrictions on users who can redeem the voucher.            products (str): Products associated with the voucher.        """        self.code = code        self.type = type        self.value = value        self.discount = discount        self.expiration_date = expiration_date        self.redemption_limit = redemption_limit        self.user_restrictions = user_restrictions        self.products = products    class VoucherSchema(ma.Schema):    """    Marshmallow schema for serializing/deserializing VoucherModel objects.    Attributes:        id (int): Primary key for the voucher.        code (str): Unique code associated with the voucher.        type (str): Type of the voucher (e.g., gift voucher, discount voucher).        value (float): Monetary value of the voucher.        discount (float): Discount amount of the voucher.        expiration_date (Date): Date when the voucher expires.        redemption_limit (int): Maximum number of times the voucher can be redeemed.        user_restrictions (str): Restrictions on users who can redeem the voucher.        products (str): Products associated with the voucher.    """    class Meta:        fields = ('id', 'code', 'type', 'value', 'discount', 'expiration_date', 'redemption_limit', 'user_restrictions', 'products')        voucher_schema = VoucherSchema()